package com.androidtechguru.top100challengefun main() {    val input = "Nitin"    println("input: $input")    println("palindrome: " + palindrome_Simple_Solution(input))    println("palindrome: " + palindrome(input))}fun palindrome_Simple_Solution(input: String): Boolean {    val filteredInput = input.filter { it.isLetterOrDigit() }.lowercase()    return filteredInput == filteredInput.reversed()}fun palindrome(input: String): Boolean {    val filteredInput = input.filter { it.isLetterOrDigit() }.lowercase()    var left = 0    var right = filteredInput.length - 1        while (left < right) {        if (filteredInput[left] != filteredInput[right])            return false        left++        right--    }        return true}