package com.androidtechguru.top100challenge.patternsfun main() {    val pattern = Pattern()    pattern.rightAngleTriangle()    pattern.invertedRightAngleTriangle()    pattern.rightAngleTriangleMirrored()}class Pattern {        fun rightAngleTriangle() {        val maxRow = 5        repeat(maxRow) { row ->            repeat(row + 1) {                print("*")            }            println()        }    }        fun invertedRightAngleTriangle() {        val maxRow = 5        repeat(maxRow) { row ->            repeat(maxRow - row) {                print("*")            }            println()        }    }        fun rightAngleTriangleMirrored() {        val maxRow = 5        repeat(maxRow) { row ->            repeat(maxRow) { col ->                if (col < maxRow - row - 1) {                    print(" ")                } else {                    print("*")                }            }            println()        }    }    }