package com.androidtechguru.top100challenge.patternsfun main() {    val pattern = Pattern()    pattern.rightAngleTriangle()}class Pattern {        fun rightAngleTriangle() {        repeat(5) { row ->            repeat(row + 1) {                print("*")            }            println()        }    }        fun invertedRightAngleTriangle() {        val maxRow = 5        repeat(maxRow) { row ->            repeat(maxRow - row){                print("*")            }            println()        }    }    }