package com.androidtechguru.top100challenge.uifun main() {    val number = -1    println("number: $number ---> binary: " + convertToBinary(number))}fun convertToBinary(num: Int): String {    if (num == 0) return "0" else if (num < 0) return ""        var number = num    val binaryString = StringBuilder()        while (number > 0) {        val remainder = number % 2        binaryString.append(remainder)        number /= 2    }        return binaryString.reverse().toString()}